declare module "*.svg" {
  import {AnyStyledComponent} from "styled-components";
    const value: string;
  export const ReactComponent: React.Component<React.SVGAttributes<SVGElement>> & AnyStyledComponent
  export default value
}

declare module "*.png" {
  const value: any;
  export default value;
}

declare module "*.jpg" {
  const value: any;
  export default value;
}

declare module "*.jpeg" {
  const value: any;
  export default value;
}

declare module "*.xml" {
  const value: any;
  export default value;
}

declare module "*.webmanifest" {
  const value: any;
  export default value;
}
